	/**
	 * Get the SVG code for the specified icon
	 *
	 * @param string $icon Icon name.
	 * @param string $group Icon group.
	 * @param array  $attr The icon attributes.
	 * @return string
	 * @since 1.0.0
	 */
	public static function get_svg( string $icon, ?string $group = 'ui', $attr = [] ): string {
		$attr = wp_parse_args(
			$attr,
			[
				'class' => '',
				'size'  => 24,
				'style' => '',
			]
		);
		$svg  = '';

		if ( null !== $group && '' !== $group && '0' !== $group ) {
			if ( 'ui' === $group ) {
				$arr = self::$ui_icons;
			} elseif ( 'social' === $group ) {
				$arr = self::$social_icons;
			} else {
				$arr = [];
			}

			$arr = apply_filters( 'blogsy_svg_icons_' . $group, $arr );

			if ( array_key_exists( $icon, $arr ) ) {
				$svg = $arr[ $icon ];
			}
		} elseif ( array_key_exists( $icon, self::$ui_icons ) ) {
				$svg = self::$ui_icons[ $icon ];
		} elseif ( array_key_exists( $icon, self::$social_icons ) ) {
			$svg = self::$social_icons[ $icon ];
		}

		if ( $svg ) {
			$size = absint( $attr['size'] );
			$repl = sprintf( '<svg width="%d" height="%d" aria-hidden="true" role="img" focusable="false" ', $size, $size );

			// Build attributes.
			unset( $attr['size'] );
			$attr_string = '';

			$attr['class'] = trim( 'blogsy-svg-icon blogsy-svg-icon--' . $icon . ' ' . $attr['class'] );

			foreach ( $attr as $name => $value ) {
				$attr_string .= esc_attr( $name ) . '="' . esc_attr( $value ) . '" ';
			}

			$svg = preg_replace( '/^<svg /', $repl, $svg );
			$svg = '<span ' . $attr_string . '>' . $svg . '</span>';
		}

		return $svg;
	}

